import React, { useState, useEffect, useCallback } from 'react';
import '../styles/Orders.css';

function Orders() {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // Simulate order status updates
    const updateOrderStatus = useCallback((id, newStatus) => {
        setOrders(currentOrders => currentOrders.map(order => 
            order._id === id ? {...order, status: newStatus} : order
        ));
    }, []);

    // Status progression
    const simulateStatusUpdate = useCallback((orders) => {
        orders.forEach(order => {
            const statuses = ['confirmed', 'being prepared', 'order is ready', 'order is out for delivery', 'order delivered'];
            statuses.forEach((status, index) => {
                setTimeout(() => updateOrderStatus(order._id, status), 5000 * (index + 1));
            });
        });
    }, [updateOrderStatus]);

    const fetchOrders = useCallback(async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5001/api/orders');
            if (!response.ok) {
                throw new Error('Something went wrong');
            }
            const data = await response.json();
            setOrders(data);
            simulateStatusUpdate(data);
        } catch (error) {
            console.error('Error fetching orders:', error);
            setError(error.message);
        } finally {
            setLoading(false);
        }
    }, [simulateStatusUpdate]);

    useEffect(() => {
        fetchOrders();
    }, [fetchOrders]);

    return (
        <div className="orders-container">
            <h1>Your Orders</h1>
            {loading ? <p>Loading...</p> : error ? <p>Error: {error}</p> : (
                <table className="orders-table">
                    <thead>
                        <tr>
                            <th>Menu Item</th>
                            <th>Quantity</th>
                            <th>Order Time</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {orders.length > 0 ? orders.map(order => (
                            <tr key={order._id}>
                                {order.items.map((item, index) => (
                                    <td key={index}>{item.menuItemId ? item.menuItemId.name : 'No item name'}</td>
                                ))}
                                <td>{order.items.reduce((total, item) => total + item.quantity, 0)}</td>
                                <td>{new Date(order.order_time).toLocaleString()}</td>
                                <td>{order.status}</td>
                            </tr>
                        )) : <tr><td colSpan="4">No orders found</td></tr>}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default Orders;
