import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/Orders.css';

function Orders() {
    const { state } = useLocation();  // Retrieve state passed via navigate
    const navigate = useNavigate();

    // Check if the state is null or undefined and handle it by showing a message or redirecting
    if (!state || !state.cartItems) {
        return (
            <div className="orders-container">
                <h1>No Recent Orders</h1>
                <button onClick={() => navigate('/')}>Back to Home</button>
            </div>
        );
    }

    const { cartItems, totalAmount } = state;

    // Ensure values are treated as numbers and formatted correctly
    const formatPrice = (price) => {
        return parseFloat(price).toFixed(2); // Ensure the price is a number and format it to two decimal places
    };

    return (
        <div className="orders-container">
            <h1>Your Order</h1>
            <table className="orders-table">
                <thead>
                    <tr>
                        <th>Item</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Total</th>
                    </tr>
                </thead>
                <tbody>
                    {cartItems.map(item => (
                        <tr key={item.id}>
                            <td>{item.title}</td>
                            <td>${formatPrice(item.price)}</td>
                            <td>{item.quantity}</td>
                            <td>${formatPrice(item.price * item.quantity)}</td>
                        </tr>
                    ))}
                    <tr>
                        <td colSpan="3">Grand Total</td>
                        <td>${formatPrice(totalAmount)}</td>
                    </tr>
                </tbody>
            </table>
            <button onClick={() => navigate('/')}>Back to Home</button>
        </div>
    );
}

export default Orders;
