import React, { useState, useEffect } from 'react';
import './MenuItems.css'; // Make sure to create and import MenuItems.css

function MenuItems() {
    const [menuItems, setMenuItems] = useState([]);
    const [cartItems, setCartItems] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchMenuItems();
    }, []);

    const fetchMenuItems = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5001/api/menu-items');
            if (!response.ok) {
                throw new Error('Something went wrong');
            }
            const data = await response.json();
            setMenuItems(data);
        } catch (error) {
            console.error('Error fetching menu items:', error);
            setError(error.message);
        }
        setLoading(false);
    };

    const handleAddToCart = (item) => {
        const newCart = {...cartItems};
        if (newCart[item._id]) {
            newCart[item._id].quantity += 1;
        } else {
            newCart[item._id] = {...item, quantity: 1};
        }
        setCartItems(newCart);
    };

    const handleRemoveFromCart = (id) => {
        const newCart = {...cartItems};
        if (newCart[id].quantity > 1) {
            newCart[id].quantity -= 1;
        } else {
            delete newCart[id];
        }
        setCartItems(newCart);
    };

    return (
        <div className="menu-items-container">
            <h1>Menu Items</h1>
            {loading ? <p>Loading...</p> : error ? <p>Error: {error}</p> : (
                <div className="menu-items-grid">
                    {menuItems.map(item => (
                        <div className="menu-item-card" key={item._id}>
                            <h3>{item.name}</h3>
                            <p>${parseFloat(item.price).toFixed(2)}</p>
                            <p>{item.description}</p>
                            <div className="menu-item-actions">
                                <button onClick={() => handleAddToCart(item)}>Add to Cart</button>
                                {cartItems[item._id] && (
                                    <button onClick={() => handleRemoveFromCart(item._id)}>
                                        Remove ({cartItems[item._id].quantity})
                                    </button>
                                )}
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default MenuItems;
