import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate instead of useHistory
import '../styles/MenuItems.css'; // Ensure the CSS file is linked correctly
import { useDispatch } from 'react-redux';
import { addItem } from '../store/cartSlice';

function MenuItems() {
    const [menuItems, setMenuItems] = useState([]);
    const [cartItems, setCartItems] = useState([]);
    const [cartTotal, setCartTotal] = useState(0);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate(); // Use the useNavigate hook for navigation

    useEffect(() => {
        fetchMenuItems();
    }, []);

    useEffect(() => {
        // Update total items in the cart
        const total = cartItems.reduce((acc, item) => acc + item.quantity, 0);
        setCartTotal(total);
    }, [cartItems]);

    const fetchMenuItems = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5001/api/menu-items');
            if (!response.ok) throw new Error('Something went wrong');
            const data = await response.json();
            setMenuItems(data.map(item => ({...item, price: parseFloat(item.price.$numberDecimal).toFixed(2)})));
        } catch (error) {
            console.error('Error fetching menu items:', error);
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    const handleCartChange = (item, increment = true) => {
        setCartItems(prev => {
            const existingItem = prev.find(cartItem => cartItem._id === item._id);
            if (existingItem) {
                return increment ? 
                    prev.map(cartItem => cartItem._id === item._id ? {...cartItem, quantity: cartItem.quantity + 1} : cartItem) :
                    prev.map(cartItem => cartItem._id === item._id ? {...cartItem, quantity: Math.max(cartItem.quantity - 1, 0)} : cartItem);
            } else if (increment) {
                return [...prev, {...item, quantity: 1}];
            }
            return prev;
        });
    };

    const proceedToCart = () => {
        navigate('/cart'); // Navigate to the cart page using useNavigate
    };

    return (
        <div className="menu-items-container">
            <h1>Menu Items</h1>
            <div className="button-group">
            <button onClick={proceedToCart} className="proceed-to-cart-btn">Proceed to Cart</button>
            </div>
            {loading ? <p>Loading...</p> : error ? <p>Error: {error}</p> : (
                <div className="menu-items-grid">
                    {menuItems.map(item => (
                        <div key={item._id} className="menu-item-card">
                            <h3>{item.name} - ${item.price}</h3>
                            <p>{item.description}</p>
                            <img src={`/Images/${encodeURIComponent(item.name)}.jpeg`} alt={item.name} />
                            <div className="button-group">
                                <button onClick={() => handleCartChange(item, true)}>Add to Cart</button>
                                {cartItems.some(ci => ci._id === item._id) && (
                                    <>
                                        <button onClick={() => handleCartChange(item, true)}>+</button>
                                        <button onClick={() => handleCartChange(item, false)}>Remove</button>
                                    </>
                                )}
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default MenuItems;
