import React, { useState, useEffect } from 'react';
import '../styles/MenuItems.css'; // Import the CSS for styling

function MenuItems() {
    const [menuItems, setMenuItems] = useState([]);
    const [cartItems, setCartItems] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchMenuItems();
    }, []);

    const fetchMenuItems = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5001/api/menu-items');
            if (!response.ok) {
                throw new Error('Something went wrong');
            }
            const data = await response.json();
            setMenuItems(data.map(item => ({
                ...item,
                price: item.price.$numberDecimal // Properly access the price
            })));
        } catch (error) {
            console.error('Error fetching menu items:', error);
            setError(error.message);
        }
        setLoading(false);
    };

    const addToCart = (item) => {
        const existingItem = cartItems.find(cartItem => cartItem._id === item._id);
        if (existingItem) {
            setCartItems(cartItems.map(cartItem => 
                cartItem._id === item._id ? {...cartItem, quantity: cartItem.quantity + 1} : cartItem
            ));
        } else {
            setCartItems([...cartItems, {...item, quantity: 1}]);
        }
    };

    const removeFromCart = (id) => {
        setCartItems(cartItems.filter(item => item._id !== id));
    };

    const increaseQuantity = (id) => {
        setCartItems(cartItems.map(item =>
            item._id === id ? {...item, quantity: item.quantity + 1} : item
        ));
    };

    const decreaseQuantity = (id) => {
        setCartItems(cartItems.map(item =>
            item._id === id ? {...item, quantity: Math.max(item.quantity - 1, 1)} : item
        ));
    };

    return (
        <div className="menu-items-container">
            <h1>Menu Items</h1>
            {loading ? <p>Loading...</p> : error ? <p>Error: {error}</p> : (
                <div className="menu-items-grid">
                    {menuItems.map(item => (
                        <div key={item._id} className="menu-item-card">
                            <h3>{item.name} - ${parseFloat(item.price).toFixed(2)}</h3>
                            <p>{item.description}</p>
                            <img 
                                src={`/Images/${encodeURIComponent(item.name)}.jpeg`} 
                                alt={item.name}
                                style={{ width: '100%', objectFit: 'cover', height: '150px' }}
                            />
                            <button onClick={() => addToCart(item)}>Add to Cart</button>
                            {cartItems.some(ci => ci._id === item._id) && (
                                <div>
                                    <button onClick={() => increaseQuantity(item._id)}>+</button>
                                    <button onClick={() => decreaseQuantity(item._id)}>-</button>
                                    <button onClick={() => removeFromCart(item._id)}>Remove</button>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default MenuItems;
