import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Home.css';

function Home() {
    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const images = [
        'Margherita Pizza.jpeg',
        'Caesar Salad.jpeg',
        'Chicken Parmesan.jpeg',
        'Grilled Salmon.jpeg',
        'Lobster Bisque.jpeg',
        'Mushroom Risotto.jpeg',
        'Ribeye Steak.jpeg',
        'Spaghetti Carbonara.jpeg',
        'Tiramisu.jpeg',
        'Vegetable Stir Fry.jpeg'
    ];

    useEffect(() => {
        const intervalId = setInterval(() => {
            setCurrentImageIndex(prevIndex => (prevIndex + 1) % images.length);
        }, 2000); // Change image every 2 seconds

        return () => clearInterval(intervalId); // Clean up the interval on component unmount
    }, [images.length]);

    return (
        <div className="home">
            <div className="content">
                <div className="text-section">
                    <h1>Delicious food delivered at your doorstep</h1>
                    <p>Yes, we have the best food in town</p>
                    <button><Link to="/menu-items">Order now</Link></button>
                </div>
                <div className="image-section">
                    <img
                        src={`/Images/${images[currentImageIndex]}`}
                        alt="Delicious food"
                        style={{ opacity: 1 }} // This will make the current image fully visible
                    />
                </div>
            </div>
        </div>
    );
}

export default Home;
