import { createSlice } from '@reduxjs/toolkit';

const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    cartItems: [],
    totalAmount: 0 // Ensure this is a number
  },
  reducers: {
    addItem(state, action) {
      const { price, quantity } = action.payload;
      const newAmount = parseFloat(price) * quantity;
      state.totalAmount += newAmount; // Adding to total amount safely
      state.cartItems.push(action.payload); // Assume adding new item directly for simplicity
    },
    deleteItem(state, action) {
      const itemIndex = state.cartItems.findIndex(item => item.id === action.payload);
      if (itemIndex !== -1) {
        const item = state.cartItems[itemIndex];
        state.totalAmount -= parseFloat(item.price) * item.quantity;
        state.cartItems.splice(itemIndex, 1);
      }
    },
    incrementItem(state, action) {
      const item = state.cartItems.find(item => item.id === action.payload);
      if (item) {
        item.quantity++;
        state.totalAmount += parseFloat(item.price);
      }
    },
    decrementItem(state, action) {
      const item = state.cartItems.find(item => item.id === action.payload);
      if (item && item.quantity > 1) {
        item.quantity--;
        state.totalAmount -= parseFloat(item.price);
      }
    },
  },
});

export const { addItem, deleteItem, incrementItem, decrementItem } = cartSlice.actions;
export default cartSlice.reducer;
