import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // to programmatically navigate
import '../styles/Orders.css';

function Orders() {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [confirmScreen, setConfirmScreen] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5001/api/orders');
            if (!response.ok) throw new Error('Something went wrong');
            const data = await response.json();
            setOrders(data);
        } catch (error) {
            console.error('Error fetching orders:', error);
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    const handleConfirmReceived = async (orderId) => {
        try {
            const response = await fetch(`http://localhost:5001/api/orders/${orderId}`, { method: 'DELETE' });
            if (!response.ok) throw new Error('Failed to delete order');
            setOrders(orders.filter(order => order._id !== orderId)); // Remove from local state
            setConfirmScreen(true); // Show confirmation screen
        } catch (error) {
            console.error('Error confirming order:', error);
            setError(error.message);
        }
    };

    if (confirmScreen) {
        return (
            <div className="confirmation-screen">
                <h1>Enjoy Your Food!</h1>
                <button onClick={() => navigate('/')}>Back to Home</button>
            </div>
        );
    }

    return (
        <div className="orders-container">
            <h1>Your Orders</h1>
            {loading ? <p>Loading...</p> : error ? <p>Error: {error}</p> : (
                <table className="orders-table">
                    <thead>
                        <tr>
                            <th>Menu Item</th>
                            <th>Quantity</th>
                            <th>Order Time</th>
                            <th>Status</th>
                            <th>Confirm</th>
                        </tr>
                    </thead>
                    <tbody>
                        {orders.map(order => (
                            <tr key={order._id}>
                                {order.items.map((item, index) => (
                                    <td key={index}>{item.menuItemId ? item.menuItemId.name : 'No item name'}</td>
                                ))}
                                <td>{order.items.reduce((total, item) => total + item.quantity, 0)}</td>
                                <td>{new Date(order.order_time).toLocaleString()}</td>
                                <td>{order.status}</td>
                                <td>
                                    {order.status === 'order delivered' && (
                                        <button onClick={() => handleConfirmReceived(order._id)}>Confirm Order Received</button>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default Orders;
