import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { deleteItem, incrementItem, decrementItem } from "../store/cartSlice";
import { getMemoizedCartItems, getMemoizedTotalAmount } from '../selectors/cartSelectors';
import "../styles/Cart.css";

const Cart = () => {
    const cartItems = useSelector(getMemoizedCartItems);
    const totalAmount = useSelector(getMemoizedTotalAmount);
    const dispatch = useDispatch();

    const handleDeleteItem = (id) => dispatch(deleteItem(id));
    const handleIncrementItem = (id) => dispatch(incrementItem(id));
    const handleDecrementItem = (id) => dispatch(decrementItem(id));

    return (
        <div className="cart-container">
            <h1>Your Cart</h1>
            {cartItems.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <>
                    <table className="cart-table">
                        <thead>
                            <tr>
                                <th>Item</th>
                                <th>Price</th>
                                <th>Quantity</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cartItems.map(item => (
                                <tr key={item.id}>
                                    <td>{item.title}</td>
                                    <td>${parseFloat(item.price).toFixed(2)}</td>
                                    <td>
                                        <div className="button-group">
                                            <button className="decrement-btn" onClick={() => handleDecrementItem(item.id)}>-</button>
                                            {item.quantity}
                                            <button className="increment-btn" onClick={() => handleIncrementItem(item.id)}>+</button>
                                        </div>

                                        <button className="remove-btn" onClick={() => handleDeleteItem(item.id)}>Remove</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <div className="cart-actions">
                        <Link to="/menu-items" className="addTOCart__btn">Continue Shopping</Link>
                        <Link to="/orders" className="addTOCart__btn">Place Order</Link>
                    </div>
                    <div className="subtotal-display">
                        Subtotal: ${parseFloat(totalAmount).toFixed(2)}
                    </div>
                </>
            )}
        </div>
    );
};

export default Cart;
