// src/store/cartSlice.js
import { createSlice } from '@reduxjs/toolkit';

const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    cartItems: [],
    totalAmount: 0,
  },
  reducers: {
    addItem(state, action) {
      const existingIndex = state.cartItems.findIndex(item => item.id === action.payload.id);
      if (existingIndex >= 0) {
        state.cartItems[existingIndex].quantity += 1;
      } else {
        state.cartItems.push({ ...action.payload, quantity: 1 });
      }
      state.totalAmount += action.payload.price;
    },
    removeItem(state, action) {
      const existingIndex = state.cartItems.findIndex(item => item.id === action.payload.id);
      if (existingIndex >= 0) {
        const existingItem = state.cartItems[existingIndex];
        state.totalAmount -= existingItem.price * existingItem.quantity;
        state.cartItems.splice(existingIndex, 1);
      }
    },
    incrementItem(state, action) {
      const existingIndex = state.cartItems.findIndex(item => item.id === action.payload);
      if (existingIndex >= 0) {
        state.cartItems[existingIndex].quantity += 1;
        state.totalAmount += state.cartItems[existingIndex].price;
      }
    },
    decrementItem(state, action) {
      const existingIndex = state.cartItems.findIndex(item => item.id === action.payload);
      if (existingIndex >= 0 && state.cartItems[existingIndex].quantity > 1) {
        state.cartItems[existingIndex].quantity -= 1;
        state.totalAmount -= state.cartItems[existingIndex].price;
      }
    },
  },
});

export const { addItem, removeItem, incrementItem, decrementItem } = cartSlice.actions;
export default cartSlice.reducer;
