import React, { useState, useEffect } from 'react';
import '../styles/MenuItems.css'; // Ensure the CSS file is linked correctly

function MenuItems() {
    const [menuItems, setMenuItems] = useState([]);
    const [cartItems, setCartItems] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchMenuItems();
    }, []);

    const fetchMenuItems = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5001/api/menu-items');
            if (!response.ok) throw new Error('Something went wrong');
            const data = await response.json();
            setMenuItems(data.map(item => ({
                ...item,
                price: parseFloat(item.price.$numberDecimal).toFixed(2)
            })));
        } catch (error) {
            console.error('Error fetching menu items:', error);
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    const addToCart = (item) => {
        const existing = cartItems.find(cartItem => cartItem._id === item._id);
        if (existing) {
            setCartItems(cartItems.map(cartItem =>
                cartItem._id === item._id ? {...cartItem, quantity: cartItem.quantity + 1} : cartItem
            ));
        } else {
            setCartItems([...cartItems, {...item, quantity: 1}]);
        }
    };

    const removeFromCart = (id) => {
        setCartItems(cartItems.filter(item => item._id !== id));
    };

    return (
        <div className="menu-items-container">
            <h1>Menu Items</h1>
            <div>Total in Cart: {cartItems.reduce((total, item) => total + item.quantity, 0)}</div>
            {loading ? <p>Loading...</p> : error ? <p>Error: {error}</p> : (
                <div className="menu-items-grid">
                    {menuItems.map(item => (
                        <div key={item._id} className="menu-item-card">
                            <h3>{item.name} - ${item.price}</h3>
                            <p>{item.description}</p>
                            <img src={`/Images/${encodeURIComponent(item.name)}.jpeg`} alt={item.name} />
                            <div className="menu-item-actions">
                                <button onClick={() => addToCart(item)}>Add to Cart</button>
                                {cartItems.some(ci => ci._id === item._id) && (
                                    <>
                                        <button onClick={() => removeFromCart(item._id)}>Remove from Cart</button>
                                    </>
                                )}
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default MenuItems;
