import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Correct import for navigation
import '../styles/MenuItems.css'; // Ensure the CSS file is linked correctly
import { useDispatch } from 'react-redux';
import { addItem } from '../store/cartSlice'; // Import addItem action

function MenuItems() {
    const [menuItems, setMenuItems] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate(); // Use the useNavigate hook for navigation
    const dispatch = useDispatch(); // Redux dispatch to handle actions

    useEffect(() => {
        fetchMenuItems();
    }, []);

    const fetchMenuItems = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5001/api/menu-items');
            if (!response.ok) throw new Error('Something went wrong');
            const data = await response.json();
            setMenuItems(data.map(item => ({
                ...item,
                price: parseFloat(item.price.$numberDecimal).toFixed(2) // Format the price correctly
            })));
        } catch (error) {
            console.error('Error fetching menu items:', error);
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    const handleAddToCart = (item) => {
        dispatch(addItem({
            id: item._id,
            title: item.name,
            price: item.price,
            quantity: 1 // Assuming default quantity to add is 1
        }));
    };

    const proceedToCart = () => {
        navigate('/cart'); // Navigate to the cart page
    };

    return (
        <div className="menu-items-container">
            <h1>Menu Items</h1>
            <button onClick={proceedToCart} className="proceed-to-cart-btn">Proceed to Cart</button>
            {loading ? <p>Loading...</p> : error ? <p>Error: {error}</p> : (
                <div className="menu-items-grid">
                    {menuItems.map(item => (
                        <div key={item._id} className="menu-item-card">
                            <h3>{item.name} - ${item.price}</h3>
                            <p>{item.description}</p>
                            <img src={`/Images/${encodeURIComponent(item.name)}.jpeg`} alt={item.name} />
                            <div className="button-group">
                                <button onClick={() => handleAddToCart(item)}>Add to Cart</button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default MenuItems;
