import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useUser } from '../contexts/UserContext';
import './Navbar.css';

function Navbar() {
    const { user, logout } = useUser();  // Destructure logout from context
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();  // Call the logout function from context
        navigate('/');  // Redirect to the home page
    };

    return (
        <nav>
            <div className="logo">
                <img src="/Images/logo.jpg" alt="Restaurant Logo" />
            </div>
            <ul>
                <li><Link to="/">Home</Link></li>
                <li><Link to="/menu-items">Menu Items</Link></li>
                <li><Link to="/orders">Orders</Link></li>
                {user && user.role === 'admin' && (
                    <li><Link to="/manage">Manage</Link></li>
                )}
                {user ? (
                    <li>
                        <button onClick={handleLogout} style={{ background: 'none', border: 'none', color: 'inherit', padding: '0', font: 'inherit', cursor: 'pointer', outline: 'inherit' }}>
                            Logout
                        </button>
                    </li>
                ) : (
                    <>
                        <li><Link to="/signup">Sign Up</Link></li>
                        <li><Link to="/login">Login</Link></li>
                    </>
                )}
            </ul>
        </nav>
    );
}

export default Navbar;
