import React from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css'; 
import { useAuth } from '../contexts/AuthContext'; // Import the useAuth hook

function Navbar() {
    const { user, logout } = useAuth(); // Get user and logout function from the AuthContext

    return (
        <nav>
            <div className="logo">
                <img src="/Images/logo.jpg" alt="Restaurant Logo" />
            </div>
            <ul>
                <li><Link to="/">Home</Link></li>
                <li><Link to="/menu-items">Menu Items</Link></li>
                {user && <li><Link to="/orders">Orders</Link></li>}
                {user && <li><Link to="/cart">Cart</Link></li>}
                {user && user.role === 'admin' && <li><Link to="/admin">Admin</Link></li>}
                {user ? (
                    <li><button onClick={logout}>Logout</button></li>  // Corrected use of logout function
                ) : (
                    <li><Link to="/login">Login</Link></li>
                )}
            </ul>
        </nav>
    );
}

export default Navbar;
