import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Team from './Team';

function Leaderboard() {
    const [teams, setTeams] = useState([]); // State to store team data

    useEffect(() => {
        // Fetch teams when component mounts
        axios.get('http://localhost:5001/api/teams') // Corrected from port 3000 to 5001
            .then(response => {
                // Assuming the API returns an array directly
                setTeams(response.data); // Set team data in state
            })
            .catch(error => console.error('Error fetching data:', error));
    }, []); // Empty dependency array means this effect runs once after the component mounts

    return (
        <div>
            <h1>Leaderboard</h1>
            {teams.map(team => (
                <Team key={team._id} team={team} />
            ))}
        </div>
    );
}

export default Leaderboard;
