import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { cartActions } from "../store/cartSlice";
import "../styles/Cart.css";

const Cart = () => {
  const cartItems = useSelector((state) => state.cart.cartItems);
  const totalAmount = useSelector((state) => state.cart.totalAmount);
  const dispatch = useDispatch();

  const deleteItem = (id) => {
    dispatch(cartActions.deleteItem(id));
  };

  const incrementItem = (id) => {
    dispatch(cartActions.incrementItem(id));
  };

  const decrementItem = (id) => {
    dispatch(cartActions.decrementItem(id));
  };

  return (
    <div className="cart-container">
      <h1>Your Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          <h5 className="mb-5">Summary of your order</h5>
          <table className="table">
            <thead>
              <tr>
                <th>Item</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item) => (
                <tr key={item.id}>
                  <td>{item.title}</td>
                  <td>${item.price}</td>
                  <td>
                    <button onClick={() => decrementItem(item.id)}>-</button>
                    {item.quantity}
                    <button onClick={() => incrementItem(item.id)}>+</button>
                  </td>
                  <td>
                    <button onClick={() => deleteItem(item.id)}>Remove</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div>
            <h6>Subtotal: ${totalAmount.toFixed(2)}</h6>
            <div className="cart-actions">
              <button className="addTOCart__btn"><Link to="/menu-items">Continue Shopping</Link></button>
              <button className="addTOCart__btn"><Link to="/checkout">Proceed to checkout</Link></button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
