import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { cartActions } from "../store/cartSlice";
import "./Cart.css";  // Assuming you have CSS for styling

const Cart = () => {
  const cartItems = useSelector((state) => state.cart.cartItems);
  const totalAmount = useSelector((state) => state.cart.totalAmount);
  const dispatch = useDispatch();

  const deleteItem = (id) => {
    dispatch(cartActions.deleteItem(id));
  };

  return (
    <div className="cart-container">
      <h1>Your Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          <h5 className="mb-5">Summary of your order</h5>
          <table className="table">
            <tbody>
              {cartItems.map((item) => (
                <tr key={item.id}>
                  <td>{item.title}</td>
                  <td>${item.price}</td>
                  <td>{item.quantity}</td>
                  <td>
                    <button onClick={() => deleteItem(item.id)}>Remove</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div>
            <h6>Subtotal: ${totalAmount}</h6>
            <div className="cart-actions">
              <button><Link to="/menu-items">Continue Shopping</Link></button>
              <button><Link to="/checkout">Proceed to checkout</Link></button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
