import React, { useState, useEffect } from 'react';
import '../styles/Orders.css';

function Orders() {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5001/api/orders');
            if (!response.ok) throw new Error('Something went wrong');
            const data = await response.json();
            setOrders(data.map(order => ({ ...order, status: 'confirmed' })));
            simulateStatusUpdate(data);
        } catch (error) {
            console.error('Error fetching orders:', error);
            setError(error.message);
        }
        setLoading(false);
    };

    const simulateStatusUpdate = (orders) => {
        orders.forEach(order => {
            const statuses = ['being prepared', 'order is ready', 'out for delivery', 'order delivered'];
            let index = 0;
            const intervalId = setInterval(() => {
                if (index < statuses.length) {
                    updateOrderStatus(order._id, statuses[index]);
                    index++;
                } else {
                    clearInterval(intervalId);
                }
            }, 5000); // Update status every 5 seconds
        });
    };

    const updateOrderStatus = (orderId, status) => {
        setOrders(orders.map(order => {
            if (order._id === orderId) {
                return { ...order, status };
            }
            return order;
        }));
    };

    return (
        <div className="orders-container">
            <h1>Your Orders</h1>
            {loading ? <p>Loading...</p> : error ? <p>Error: {error}</p> : (
                <table className="orders-table">
                    <thead>
                        <tr>
                            <th>Menu Item</th>
                            <th>Quantity</th>
                            <th>Status</th>
                            <th>Order Time</th>
                        </tr>
                    </thead>
                    <tbody>
                        {orders.map(order => (
                            <tr key={order._id}>
                                {order.items.map((item, index) => (
                                    <td key={index}>{item.menuItemId.name}</td>
                                ))}
                                <td>{order.items.reduce((total, item) => total + item.quantity, 0)}</td>
                                <td>{order.status}</td>
                                <td>{new Date(order.order_time).toLocaleString()}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default Orders;
