import React, { useState, useEffect } from 'react';

function Leaderboard() {
    const [teams, setTeams] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5001/api/teams')
            .then(response => response.json())
            .then(data => {
                setTeams(data);
            })
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    const getRankIcon = (rank) => {
        switch(rank) {
            case 1:
                return '/Images/golden.jpg';
            case 2:
                return '/Images/silver.jpg';
            case 3:
                return '/Images/bronze.jpg';
            default:
                return null;
        }
    };

    return (
        <div>
            <h1>Leaderboard</h1>
            <table className="table">
                <thead>
                    <tr>
                        <th>RANK</th>
                        <th>TEAM NAME</th>
                        <th>TOTAL GAMES PLAYED</th>
                        <th>SCORE</th>
                    </tr>
                </thead>
                <tbody>
                    {teams.map((team, index) => (
                        <tr key={team._id}>
                            <td>
                                {getRankIcon(index + 1) ? (
                                    <img src={getRankIcon(index + 1)} alt={`Rank ${index + 1}`} style={{ width: '30px', height: '30px' }} />
                                ) : (
                                    index + 1
                                )}
                            </td>
                            <td className="team-name">
                                <img src={team.avatarUrl || '/Images/1.jpg'} alt={team.name} className="avatar" />
                                {team.name}
                            </td>
                            <td>{team.gamesPlayed}</td>
                            <td>{team.score}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Leaderboard;
