import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import { deleteItem, incrementItem, decrementItem } from "../store/cartSlice";
import "../styles/Cart.css";

const Cart = () => {
    const cartItems = useSelector((state) => state.cart.cartItems);
    const totalAmount = useSelector((state) => state.cart.totalAmount);
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleDeleteItem = (id) => {
        dispatch(deleteItem(id));
    };

    const handleIncrementItem = (id) => {
        dispatch(incrementItem(id));
    };

    const handleDecrementItem = (id) => {
        dispatch(decrementItem(id));
    };

    const handlePlaceOrder = async () => {
        const orderDetails = {
            items: cartItems.map(item => ({ menuItemId: item.id, quantity: item.quantity })),
            tableNumber: 1 // Example table number
        };

        try {
            const response = await fetch('http://localhost:5001/api/orders', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(orderDetails)
            });
            if (response.ok) {
                navigate('/orders'); // Navigate to orders page upon successful order
            } else {
                throw new Error('Failed to place order');
            }
        } catch (error) {
            console.error('Error placing order:', error);
        }
    };

    return (
        <div className="cart-container">
            <h1>Your Cart</h1>
            {cartItems.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <>
                    <h5 className="mb-5">Summary of your order</h5>
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Item</th>
                                <th>Price</th>
                                <th>Quantity</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cartItems.map((item) => (
                                <tr key={item.id}>
                                    <td>{item.title}</td>
                                    <td>${item.price}</td>
                                    <td>
                                        <button onClick={() => handleDecrementItem(item.id)}>-</button>
                                        {item.quantity}
                                        <button onClick={() => handleIncrementItem(item.id)}>+</button>
                                    </td>
                                    <td>
                                        <button onClick={() => handleDeleteItem(item.id)}>Remove</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <div>
                        <h6>Subtotal: ${totalAmount.toFixed(2)}</h6>
                        <div className="cart-actions">
                            <button onClick={handlePlaceOrder} className="addTOCart__btn">Place Order</button>
                        </div>
                    </div>
                </>
            )}
        </div>
    );
};

export default Cart;
