import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { deleteItem, incrementItem, decrementItem } from "../store/cartSlice";
import "../styles/Cart.css";

const Cart = () => {
    const cartItems = useSelector(state => state.cart.cartItems);
    const totalAmount = useSelector(state => state.cart.totalAmount);
    const dispatch = useDispatch();

    const handleDeleteItem = (id) => dispatch(deleteItem(id));
    const handleIncrementItem = (id) => dispatch(incrementItem(id));
    const handleDecrementItem = (id) => dispatch(decrementItem(id));

    return (
        <div className="cart-container">
            <div className="title">Shopping Bag</div>
            {cartItems.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                cartItems.map(item => (
                    <div className="item" key={item.id}>
                        <div className="buttons">
                            <button className="delete-btn" onClick={() => handleDeleteItem(item.id)}></button>
                            <button className="like-btn"></button>
                        </div>
                        <div className="image">
                            <img src={item.imageUrl} alt={item.title} />
                        </div>
                        <div className="description">
                            <span>{item.title}</span>
                            <span>{item.description}</span>
                        </div>
                        <div className="quantity">
                            <button className="minus-btn" onClick={() => handleDecrementItem(item.id)}>
                                <img src="/minus.svg" alt="" />
                            </button>
                            <input type="text" value={item.quantity} readOnly />
                            <button className="plus-btn" onClick={() => handleIncrementItem(item.id)}>
                                <img src="/plus.svg" alt="" />
                            </button>
                        </div>
                        <div className="total-price">${item.price.toFixed(2)}</div>
                    </div>
                ))
            )}
            <div className="subtotal-display">
                Subtotal: ${totalAmount.toFixed(2)}
            </div>
        </div>
    );
};

export default Cart;
