import React, { useState, useEffect } from 'react';
import '../styles/MenuItems.css'; 

function MenuItems() {
    const [menuItems, setMenuItems] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchMenuItems();
    }, []);

    const fetchMenuItems = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5001/api/menu-items');
            if (!response.ok) {
                throw new Error('Something went wrong');
            }
            const data = await response.json();
            // Ensure price is converted correctly if stored as {$numberDecimal: "12"}
            const itemsWithFormattedPrice = data.map(item => ({
                ...item,
                price: parseFloat(item.price.$numberDecimal).toFixed(2) // Convert the Decimal128 object to a formatted string
            }));
            setMenuItems(itemsWithFormattedPrice);
        } catch (error) {
            console.error('Error fetching menu items:', error);
            setError(error.message);
        }
        setLoading(false);
    };

    return (
        <div>
            <h1>Menu Items</h1>
            {loading ? <p>Loading...</p> : error ? <p>Error: {error}</p> : (
                <div>
                    {menuItems.map(item => (
                        <div key={item._id} style={{ margin: "20px", border: "1px solid #ccc", padding: "10px" }}>
                            <h3>{item.name} - ${item.price}</h3> {/* Display the correctly formatted price */}
                            <p>{item.description}</p>
                            <img 
                                src={`/Images/${encodeURIComponent(item.name)}.jpeg`} 
                                alt={item.name}
                                style={{ width: "100%", height: "auto" }}
                            />
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default MenuItems;
