import { createSlice } from '@reduxjs/toolkit';

const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    cartItems: [],
    totalAmount: 0
  },
  reducers: {
    addItem(state, action) {
      const existingIndex = state.cartItems.findIndex(item => item.id === action.payload.id);
      if (existingIndex >= 0) {
        state.cartItems[existingIndex].quantity += 1;
      } else {
        state.cartItems.push({ ...action.payload, quantity: 1 });
      }
      state.totalAmount = state.cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
    },
    deleteItem(state, action) {
      state.cartItems = state.cartItems.filter(item => item.id !== action.payload);
      state.totalAmount = state.cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
    },
    incrementItem(state, action) {
      const existingIndex = state.cartItems.findIndex(item => item.id === action.payload);
      if (existingIndex >= 0) {
        state.cartItems[existingIndex].quantity += 1;
      }
      state.totalAmount = state.cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
    },
    decrementItem(state, action) {
      const existingIndex = state.cartItems.findIndex(item => item.id === action.payload);
      if (existingIndex >= 0 && state.cartItems[existingIndex].quantity > 1) {
        state.cartItems[existingIndex].quantity -= 1;
      }
      state.totalAmount = state.cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
    },
  },
});

export const cartActions = cartSlice.actions;
export default cartSlice.reducer;
