import React, { useState, useEffect } from 'react';

function Cart() {
    const [cartItems, setCartItems] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchCartItems();
    }, []);

    const fetchCartItems = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5001/api/cart');
            if (!response.ok) {
                throw new Error('Something went wrong');
            }
            const data = await response.json();
            setCartItems(data);
        } catch (error) {
            console.error('Error fetching cart items:', error);
            setError(error.message);
        }
        setLoading(false);
    };

    // Define removeItemFromCart function
    const removeItemFromCart = async (id) => {
        try {
            const response = await fetch(`http://localhost:5001/api/cart/${id}`, {
                method: 'DELETE'
            });
            if (!response.ok) {
                throw new Error('Failed to delete the item');
            }
            // Update the state to remove the item from the UI
            setCartItems(prevItems => prevItems.filter(item => item._id !== id));
        } catch (error) {
            console.error('Error removing item from cart:', error);
            setError(error.message);
        }
    };

    return (
        <div>
            <h1>Your Cart</h1>
            {loading ? <p>Loading...</p> : error ? <p>Error: {error}</p> : (
                <table>
                    <thead>
                        <tr>
                            <th>Item</th>
                            <th>Quantity</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {cartItems.map(item => (
                            <tr key={item._id}>
                                <td>{item.menuItemId.name}</td>
                                <td>{item.quantity}</td>
                                <td>
                                    <button onClick={() => removeItemFromCart(item._id)}>Remove</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default Cart;
