import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Orders.css';

function Orders() {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [confirmScreen, setConfirmScreen] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5001/api/orders');
            if (!response.ok) throw new Error('Something went wrong');
            const data = await response.json();
            const ordersWithTimers = data.map(order => ({
                ...order,
                timer: setInterval(() => updateStatus(order._id), 5000)
            }));
            setOrders(ordersWithTimers);
        } catch (error) {
            console.error('Error fetching orders:', error);
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    const updateStatus = (orderId) => {
        setOrders(currentOrders => currentOrders.map(order => {
            if (order._id === orderId) {
                const nextStatusIndex = order.statusIndex + 1;
                const statuses = ['confirmed', 'being prepared', 'order is ready', 'order is out for delivery', 'order delivered'];
                if (nextStatusIndex < statuses.length) {
                    return { ...order, status: statuses[nextStatusIndex], statusIndex: nextStatusIndex };
                } else {
                    clearInterval(order.timer);
                }
            }
            return order;
        }));
    };

    useEffect(() => {
        return () => {
            // Cleanup timers when component unmounts
            orders.forEach(order => {
                if (order.timer) {
                    clearInterval(order.timer);
                }
            });
        };
    }, [orders]);

    if (confirmScreen) {
        return (
            <div className="confirmation-screen">
                <h1>Enjoy Your Food!</h1>
                <button onClick={() => navigate('/')}>Back to Home</button>
            </div>
        );
    }

    return (
        <div className="orders-container">
            <h1>Your Orders</h1>
            {loading ? <p>Loading...</p> : error ? <p>Error: {error}</p> : (
                <table className="orders-table">
                    <thead>
                        <tr>
                            <th>Menu Item</th>
                            <th>Quantity</th>
                            <th>Order Time</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {orders.map(order => (
                            <tr key={order._id}>
                                {order.items.map((item, index) => (
                                    <td key={index}>{item.menuItemId ? item.menuItemId.name : 'No item name'}</td>
                                ))}
                                <td>{order.items.reduce((total, item) => total + item.quantity, 0)}</td>
                                <td>{new Date(order.order_time).toLocaleString()}</td>
                                <td>{order.status}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default Orders;
