import { createSelector } from 'reselect';

// Assuming you have a state structure where cartItems are part of a cart object
const getCartItems = state => state.cart.cartItems;
const getTotalAmount = state => state.cart.totalAmount;

// Correct use of createSelector to compute total price in cart
export const getTotalCartValue = createSelector(
  [getCartItems],
  (cartItems) => cartItems.reduce((total, item) => total + item.price * item.quantity, 0)
);

// If needed, combining multiple pieces of state effectively
export const getCartSummary = createSelector(
  [getCartItems, getTotalAmount],
  (cartItems, totalAmount) => ({
    itemCount: cartItems.length,
    totalAmount
  })
);
