import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Orders.css';

function Orders() {
    const navigate = useNavigate();
    const [orders, setOrders] = useState([]);
    const [confirmScreen, setConfirmScreen] = useState(false);

    useEffect(() => {
        fetchOrders();
        updateStatusAutomatically();
    }, []);

    const fetchOrders = async () => {
        try {
            const response = await fetch('/api/orders');  // Make sure the URL matches your backend route
            const data = await response.json();
            setOrders(data);
        } catch (error) {
            console.error('Failed to fetch orders:', error);
        }
    };

    const updateStatusAutomatically = () => {
        const statusSteps = ['confirmed', 'being prepared', 'order is ready', 'order is out for delivery', 'order delivered'];

        orders.forEach(order => {
            let index = order.statusIndex || 0;
            const intervalId = setInterval(() => {
                if (index < statusSteps.length) {
                    setOrders(currentOrders => currentOrders.map(o => 
                        o._id === order._id ? { ...o, status: statusSteps[index], statusIndex: index++ } : o
                    ));
                } else {
                    clearInterval(intervalId);
                }
            }, 5000);
        });
    };

    const handleConfirmReceived = (orderId) => {
        setOrders(orders => orders.filter(order => order._id !== orderId));
        setConfirmScreen(true); // Show confirmation screen
    };

    if (confirmScreen) {
        return (
            <div className="confirmation-screen">
                <h1>Enjoy Your Food!</h1>
                <button onClick={() => navigate('/')}>Back to Home</button>
            </div>
        );
    }

    return (
        <div className="orders-container">
            <h1>Your Orders</h1>
            <table className="orders-table">
                <thead>
                    <tr>
                        <th>Menu Item</th>
                        <th>Quantity</th>
                        <th>Order Time</th>
                        <th>Status</th>
                        <th>Confirm</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map(order => (
                        <tr key={order._id}>
                            <td>{order.items.map(item => item.menuItemId.name).join(", ")}</td>
                            <td>{order.items.reduce((sum, item) => sum + item.quantity, 0)}</td>
                            <td>{new Date(order.order_time).toLocaleString()}</td>
                            <td>{order.status}</td>
                            <td>
                                {order.status === 'order delivered' && (
                                    <button onClick={() => handleConfirmReceived(order._id)}>
                                        Confirm Order Received
                                    </button>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Orders;
