import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useUser } from '../contexts/UserContext';
import './Navbar.css';

function Navbar() {
    const { user, logout } = useUser();  // Destructure user and logout from context
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();  // Call the logout function from context
        navigate('/');  // Redirect to the home page
    };

    return (
        <nav>
            <div className="logo">
                <img src="/Images/logo.jpg" alt="Restaurant Logo" />
            </div>
            <ul>
                {/* Check if user is defined and logged in */}
                {user ? (
                    <>
                        {/* Common link for all logged-in users */}
                        <li><Link to="/menu-items">Menu Items</Link></li>

                        {/* Show orders link to all users */}
                        <li><Link to="/orders">Orders</Link></li>

                        {/* Additional admin-specific link */}
                        {user.role === 'admin' && (
                            <li><Link to="/manage">Manage</Link></li>
                        )}

                        {/* Additional customer-specific links */}
                        {user.role === 'customer' && (
                            <li><Link to="/cart">Cart</Link></li>
                        )}

                        {/* Logout link for all logged-in users */}
                        <li>
                            <button onClick={handleLogout} style={{ background: 'none', border: 'none', color: 'inherit', padding: '0', font: 'inherit', cursor: 'pointer', outline: 'inherit' }}>
                                Logout
                            </button>
                        </li>
                    </>
                ) : (
                    // When no user is logged in, do not display any links
                    <>
                    </>
                )}
            </ul>
        </nav>
    );
}

export default Navbar;
