import { createSelector } from 'reselect';

const cartState = state => state.cart;

export const getMemoizedCartItems = createSelector(
  [cartState],
  (cart) => cart.cartItems
);

export const getMemoizedTotalAmount = createSelector(
  [cartState],
  (cart) => cart.cartItems.reduce((total, item) => total + item.price * item.quantity, 0)
);
